INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'de62e78e-b8a0-7f3a-a76a-000000003294', N'Concert Consorto Etereo', N'Het oudemuziekensemble Consorto Etereo werd door Jole De Baerdemaeker, Adelheid Glatt, Johanna Lambrechts en Liam Fennelly opgericht. Hun passie voor de historische uitvoeringspraktijk en het musicologisch onderzoek bracht hen samen en resulteerde in een dynamisch ensemble dat, naargelang het repertoire, met wisselende bezettingen voor de dag komt. Voor dit concert nemen ze twee muzikale generaties onder de loep, om de evoluties binnen de rijke Franco-Vlaamse polyfone traditie in de verf te zetten. Hiervoor gingen ze snuisteren in de vele chansons die voor Margaretha van Oostenrijk en later voor haar neefje Karel V geschreven werden. Na vele intense repetities presenteren zij u een gevarieerd programma van drie- en vierstemmige polyfonie uit de chansonniers van het Mechelse hof van Savoye en het Gentse Prinsenhof.  Sopraan is deze keer Emma Coopman. Tickets bestellen bij consorto-etereo@telenet.be', CAST(18 AS Decimal(2, 0)), CAST(N'2022-07-23T18:00:00.000' AS DateTime), CAST(N'2022-07-23T19:00:00.000' AS DateTime), N'5ae81f4b-0df0-482c-9783-b38cd25c5906')
