INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'e120fd75-65bc-4e60-9543-6c419805109c', N'Stamp, Stop, Sta, Stil! - Onder het Buro & Willemijn Böcker', N'In ‘Stamp, Stop, Sta, Stil’ gaan peuters, kleuters en hun ouders op ontdekkingsreis in de natuur. Aan de hand van hilarische, spannende scènes en mooie, vrolijke liedjes wordt onderzocht wat voor bijzonders er buiten allemaal te vinden is en hoe je de weg naar huis weer kunt vinden. Ollie woont met haar ouders in een huisje aan de rand van het bos en vandaag gaat ze op ontdekkingsreis. Met haar regenlaarzen aan, een verrekijker om haar nek en een vangnet bij de hand gaat ze naar buiten. Daar ontdekt ze de kleine beestjes in het gras, de bloemen in het weiland en nog veel meer mooie dingen.  Ze stampt door de plassen! Buiten ruikt het zo lekker! En alles maakt geluid. Al stampend dwaalt ze steeds verder het bos in. Maar in de schaduw van het bos is het best een beetje donker en hoe vindt ze eigenlijk de weg terug naar huis?', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
