INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'e18210cb-0387-5b66-8397-000000002910', N'Rondleiding met gids in Plantentuin Universiteit Gent. Natte voeten.', N'Water is een apart biotoop waaraan een aantal planten in de loop van de evolutie zijn aangepast om er te kunnen overleven. Tijdens deze rondleiding bekijken we diverse  oever- en waterplanten en bespreken we hun typische eigenschappen.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-22T14:30:00.000' AS DateTime), CAST(N'2022-07-22T16:00:00.000' AS DateTime), N'9ce2ac40-e3fb-41b4-97bd-54a5db0a3b5c')
