INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'e5bed21d-6539-40c7-90ec-281cb96cc55b', N'Aisthetes, the one who perceives door Sulis_Vocaal ensemble. Renaissance muziek.', N'Begin dit jaar kwamen vijf amateur-zangers samen in de kunstacademie van Brugge voor een muzikaal avontuur. Het doel? Aantonen dat de muziek van William Byrd (1543-1623) kan gebracht worden door enthousiaste liefhebbers voor een gevarieerd publiek.  Openbaar repeteren op verschillende locaties lokte medereizigers. Het ensemble groeide en kreeg een instrumentale aanvulling met getalenteerde strijkers. Zo diende de mogelijkheid zich aan om de luister-ervaring meer toegankelijk te maken. Sulis blijft trouw aan de composities maar op basis van intuïtief aanvoelen wordt de muziek een dialoog tussen zangers en strijkers.  Deze intuïtie zou Sulis graag delen met de luisteraars. Bezoekers worden aangemoedigd om tijdens de concerten zelf creatief te zijn. Muziek en beeld gaan immers samen. Daarom krijgt het publiek de kans om de ervaring op tekenpapier te vertalen.   Een muzikale reis met Sulis wordt zo een artistieke totaal-ervaring voor alle metgezellen!', NULL, NULL, NULL, NULL)
GO
INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'e5d0a4d2-0aae-2571-c200-000000002388', N'Swing – Circus Ronaldo', N'Swing maakt de scène vrij voor een nieuwe generatie van Circus Ronaldo. Nanosh Ronaldo creëert de sfeer van circus en variété uit de jaren ‘40, samen met een groep jonge circusartiesten.    Verwacht je aan groots cabaretspektakel met kleine knipogen, een vleugje verleiding en onvervalste swingmuziek. De aanstekelijke speelsheid, de heerlijke dynamiek en de blije opgewektheid van Circus Ronaldo’s jonge garde laat je opnieuw verliefd worden op circus zoals je het al lang niet meer zag.', CAST(15 AS Decimal(2, 0)), CAST(N'2022-07-17T20:30:00.000' AS DateTime), CAST(N'2022-07-17T22:00:00.000' AS DateTime), N'0b99db5d-f20e-4498-9b0c-371d03923ea5')
