INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'e8e12b01-6ab2-5ffa-5fe0-000000005457', N'Openingsevenement Gentse Feesten', N'Gentenaars en andere enthousiaste bezoekers worden verwelkomd op de Voorhoutkaai om samen de feesten in te zingen, onder deskundige begeleiding van Allez, Chantez!  Daarnaast zal de oude Belgacomtoren ook een centrale rol spelen. In de beste Gentse traditie zal er iets met pyrotechnische technieken gebeuren. De volledige invulling blijft nog even geheim maar het belooft alleszins de moeite te worden. Ook dit jaar gaan we voor een unieke en éénmalige beleving. We kunnen jullie dus alleen maar aanraden om op tijd te komen want de capaciteit is beperkt. De voorstelling start stipt om 19.00 uur.  Het spektakel is uiteraard gratis.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-15T19:00:00.000' AS DateTime), CAST(N'2022-07-16T05:59:59.000' AS DateTime), N'3ba9b54d-be74-49a6-8c1e-980211e8101d')
