INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'ea48073c-ff13-4689-bb5a-137041d39f9a', N'K.M. De Melomanen - De Familie Potaarde in Spanje - Een wervelende komedie van Romain Deconinck', N'Net als elk jaar brengt KM De Melomanen een wervelende komedie van Romain Deconinck. Het jaar 1964. Alfred, de schoonzoon van Stien Potaarde is Europees judo kampioen geworden bij de politie. Als beloning mag hij samen met zijn hele familie en de familie Pycke op reis naar Spanje. Klein probleempje echter, in de reis is geen verblijf inbegrepen. Onder een verzengende hitte moet het gezelschap op zoek naar een passend, en liefst niet te duur logement. Gelukkig komt de lokale politie ter hulp en vinden ze een klein en vooral goedkoop hotel.  Ongewild helpen ze de politie in de zoektocht naar een onverlaat die nietsvermoedende toeristen besteelt.  Romain Deconinck schreef 13 stukken met de familie Potaarde in de hoofdrol. Dit stuk is het laatste en bovendien een van de beste uit deze reeks.', NULL, NULL, NULL, NULL)
