INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'ee3c4ee4-4fa8-4edf-9cde-f315b1880120', N'Verdwalen - Jahon (goocheltheater)', N'Een jongen, een appel en een bos, Een boom, een specht tikt erop los. Een jongen, een landkaart, ronddwalen, Landkaart verscheurd, verdwalen.  In ‘Verdwalen’ neemt Jahon je mee in een woordenloos verhaal vol goochelen, muziek en poppenspel.  Met zijn goocheltrucs als bindmiddel ga je mee op wandeling doorheen een bijzonder bos waarin zelfs het eten van een appel een uitdaging is.  Met zijn acts was Jahon oa te zien in Bobbejaanland, Theater op de markt, Kinepolis, … en als huisgoochelaar van VTM Kids.', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
