INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'f0916e1f-ca6c-95ad-19d6-000000004548', N'De Avonturen van Dik en Pier - Dik & Pier', N'Dik en Pier zijn beste vrienden en wonen bij elkaar in de straat. Ze zijn altijd samen. Ze kletsen, spelen en lachen. Dik stelt Pier gerust als hij bang is in het donker. Pier troost Dik als hij verdrietig is omdat zijn lieve hondje er niet meer is. En elke dag maken Dik en Pier muziek. Ze zingen uit volle borst en spelen daarbij op hun gitaar, mandoline, piano, ukelele, accordeon, marimba en djembé. Telkens als het lekker gaat komt de boze buurman klagen en roept dat het stil moet zijn in een nette straat.  Muzikaal hebben Dik & Pier het werk van Tom Waits als basis genomen. De organische en pure liedjes van Waits lenen zich uitstekend als muzikaal uitgangspunt. Tekstueel hebben zij zich laten inspireren door de prachtige kindergedichtenbundels van Theo Olthuis (bekend van o.m. Sesamstraat, Klokhuis en Koekeloere).  Levensliedjes voor onvolwassenen', CAST(13 AS Decimal(2, 0)), CAST(N'2022-07-18T15:30:00.000' AS DateTime), CAST(N'2022-07-18T16:30:00.000' AS DateTime), N'3f52ebae-eb86-4eb7-ac24-36003efd7365')
