INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'f1d4d167-1959-4741-97b4-0feceb4d6103', N'Ridder Ridder - Vreeken en Van der Pijl', N'"Filip en Fiona willen het beste sprookje ooit bouwen;  ze hebben al een ridder, genaamd ridder Ridder. Hij moet en zal de schoone deerne uit een groot, eng drakenslot bevrijden. Dat is wat ridders doen, toch? Op zijn tocht wordt Ridder door wonderlijke wezens geholpen of juist gedwarsboomd – de bomen bijvoorbeeld, die liggen soms dwars. Er is alleen één probleem: Filip en Fiona zijn maar met z’n tweeën;  wie moeten al die wezens spelen dan? Als ze nou heel snel van rol wisselen... Dan moet Filip wel de jurk van de prinses aan zodat Fiona de oude dwergtrol kan spelen. Maar een ridder in een jurk, en meisje dat een trol speelt: kan dat wel? Met humor en behendigheid zetten twee acteurs deze voorstelling als een bouwpakket in elkaar. Al gauw wordt duidelijk dat de standaard genderrollen losgelaten moeten worden om het verhaal van Ridder tot leven te brengen. En wie weet ontdekken Fiona en Filip kanten van zichzelf die ze nog niet kenden."', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
