INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'f269953b-2892-4a76-baec-45185b5f4e87', N'Wandeling Sporen van industrieel Gent', N'Slechts weinig mensen kunnen zich het Gent van honderd jaar geleden inbeelden, vol rokende fabrieksschoorstenen en dichtbevolkte beluiken. Die bloeiende industriestad van toen laat ons vandaag kennismaken met enkele erfgoedpareltjes. Die plekken werden verzameld op een fikse wandelkaart. Een gids neemt je mee op wandel langs een aantal van deze erfgoedpareltjes. De wandeling start aan het Industriemuseum, waar tot 1975 een textielfabriek huisde, en leidt ons verder via de Vrijdagmarkt naar de Zuivelbrug, het Veerleplein en de Graslei om dan via het François Laurentplein en de Reep te eindigen vlakbij Portus Ganda, aan zwembad Van Eyck.  Onderweg maak je kennis met heel wat gebouwen en weetjes uit het geweldige industrieverleden van Gent. Na de tour brengt de gids je terug tot bij het Industriemuseum. Wie wil, kan ook de wandelroute afwerken met de wandelkaart in de hand en zo nog enkele extra weetjes meepikken.', NULL, NULL, NULL, NULL)
