INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'f3fa7ccc-1225-496c-9fca-25f96d75d1f0', N'Workshop Vilt je eigen portemonnee', N'Vilten is een oeroude techniek. Door wol met je handen te bewerken met water en zeep krijg je vilt. En dat vilt vind je vandaag in tal van producten. Van juwelen en tassen tot versieringen en zelfs tapijten. Tijdens deze workshop maak je eerst kennis met de basisprincipes van de vilttechniek. Daarna stroop je de mouwen op en ga je zelf aan de slag. We maken een kleurrijk lapje en nadien naaien we het om tot een eigen unieke vilten portemonnee.   De deelnameprijs is inclusief begeleiding, materialen en een koekje/drankje.  OVER DE BEGELEIDERS   Anse Heestermans is gepassioneerd door de eindeloze mogelijkheden van textiel. Als leerkracht Textiele kunsten aan de academie van Temse hoopt ze zo de microbe van dit veelzijdige medium door te geven. Ze houdt ervan technieken en materialen te onderzoeken en zich eigen te maken.  Zo komt ze tot nieuwe inzichten en verrassende creaties.', NULL, NULL, NULL, NULL)
