INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'f462c338-4905-4a9b-b5c8-75839c28de39', N'Zoektochtboekje: Van Eyck in Gent', N'Tijdens deze zoektocht verneem je tal van wetenswaardigheden over de stad, over Jan Van Eyck, over het Lam Gods en zeker ook over de merkwaardige diefstal van het nog steeds niet teruggevonden paneel met de Rechtvaardige Rechters. Het parcours voert je langs plaatsen en monumenten die Jan Van Eyck gezien heeft toen hij in Gent verbleef in de jaren 1420-1430, of die vandaag nog aan de schilder of aan het Lam Gods herinneren. Onderweg word je uitgedaagd om vragen of puzzels op te lossen en scherpen zoekopdrachten je blik. Je ontdekt details waar je anders achteloos aan voorbij loopt en komt aan de belangrijkste bezienswaardigheden van het historische stadscentrum voorbij.', NULL, NULL, NULL, N'62326b09-ddeb-4795-aee1-99ad45e2d07f')
