INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'f75e1308-69d2-1c79-e97e-000000004986', N'WORKSHOP: Vind je spot! Musical Improtheater op Alles Kapot Festival', N'Door: Bart Bracke, improvisator bij Caque & FUS!, en grote fan van Musical Improv. Samen met pianist-improvisator Peter Van Riet, bekend van Peter Van Riet. ----------- De moeilijkste stap bij een lied improviseren in improvisatietheater. Wat ga ik daar eigenlijk doen vooraan? En over wat gaan we dan wel zingen? En wat als het publiek het afgrijselijk vindt? Deze workshop gaat niet naar alle extraatjes en vrolijke vetrandjes die we met een geïmproviseerde musical associëren, zoals rijmen, zangacrobatie of choreo. Deze workshop gaat naar zijn naakte essentie: iemand die vol emotie de stap zet in de spotlight. Omdat niet zingen geen optie meer is.', CAST(35 AS Decimal(2, 0)), CAST(N'2022-07-23T14:00:00.000' AS DateTime), CAST(N'2022-07-24T05:59:59.000' AS DateTime), N'69f36424-c678-46bb-8d9c-25623a148300')
