INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'fa5b0fe3-5011-8319-1505-000000000855', N'Faust ofte krakeling beneden de louteringsberg', N'Pjeroo Roobjee bewerkte deze klassieker naar -in zijn woorden- een représentation waarin hemel en helle vergaan, waarin ieder verdwijnt en verschijnt in schoon en ander kostuums en het hellevuurwerk vol sulfer en pek en schoon blommekees aan het verbouwereerde publiek wordt voorgeschoteld! Dat allemaal in een decorke van grootse kastelen en uitgestrekte velden, bossen, hemelse kamers, krochten van prison, en hellevuur waar ge u wreed aan brandt vol pijn en alteratie.  Over lust en liefde (of wat daarvoor moet doorgaan), over liegen en bedriegen en moord en kindermoord en andere leuke dingen. Enfin, over de couleuren van het leven.  Tekst: Pjeroo Roobjee - Regie: Etienne De Ruyck - Spel: Lynn Ryssen, Pieter Theunissen, Heleen Van Melckebeke, Leander Vergote - Productieassistentie: Leena Van den Bergh - Soundscape: Joris Van Maldeghem - Decor: Jonas Dusselier - Foto: Jean-Pierre Fack', CAST(12 AS Decimal(2, 0)), CAST(N'2022-07-16T20:00:00.000' AS DateTime), CAST(N'2022-07-17T05:59:59.000' AS DateTime), N'5a65bece-4082-45bf-a5b4-7ef611ad6edd')
