INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'fac09b62-8ed1-e378-9013-000000001191', N'Rondvaarten in North Sea Port', N'Heb je altijd al eens de haven van North Sea Port van binnenuit willen ontdekken?  Een unieke ervaring waarbij je de Belgische kant van de haven beleeft vanaf het water.   Een gids geeft je toelichting over (nieuwe) bedrijven tot aan historische en nieuwe ontwikkelingen.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-22T10:30:00.000' AS DateTime), CAST(N'2022-07-22T12:00:00.000' AS DateTime), N'066cf36e-8f42-4249-a225-a6a0befc94e3')
