INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'feacd328-c0ee-2f1d-2dce-000000004488', N'Zoemkwaakkriebeltjes - Theater Hutsepot', N'Een heel visuele en verrassend muzikale voorstelling voor de kleinsten.  Een jongen helemaal alleen in een groot maar zacht bos. Hij is op zoek naar zijn verloren bal. Maar die vindt hij niet onmiddellijk. Hij vindt wel een wegwijsbij en dansende eenden. En wie woont daar in de boom, naast die lieve spin? Hij ontdekt hoe sprinkhanen leren springen en hij komt te weten dat muggen kleuren kunnen mengen. Maar of hij ooit zijn bal nog terug vindt? Om dat te weten te komen, moet je beslist komen kijken!', CAST(11 AS Decimal(2, 0)), CAST(N'2022-07-18T10:00:00.000' AS DateTime), CAST(N'2022-07-18T11:00:00.000' AS DateTime), N'2fc32c50-e865-4526-a92d-ba78f6643e27')
