INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'feb4991d-6849-c7b6-1c29-000000001740', N'Gegidste rondleiding over de Stroppendragers en Keizer Karel', N'"U verneemt alles over de Stroppendragers, keizer Karel en diens stormachtige relatie met zijn geboortestad Gent. De rondleiding wordt gegeven door een Stroppendrager in witte tabbaard, bij aanvang van de wandeling krijgt u een authentieke Gentse strop. Gidsen, lid van de Gilde van de Stroppendragers:  Philippe De Gusseme & Marc Symoens Rondleidingen op vrijdag 22 juli 2022 en zaterdag 23 juli 2022, telkens start om 10u30 en 14u00, Duur: 1,5 uur met de start aan de trappen van het stadhuis Belfortstraat Prijs: 10 euro per persoon Inschrijven kan door een mail te sturen naar inschrijvingen@stroppendragers.gent of bel +32 472 32 82 95 Staelens Jean. Vermeld je gegevens, aantal personen en gewenst tijdschema. Gelieve het bedrag over te schrijven naar rekeningnummer: IBAN BE60 8902 3406 4970 met als mededeling: ""NAAM"" + ""AANTAL"" + RONDLEIDING GF2022 Na ontvangst van het overgeschreven bedrag ontvangt u een bevestigingsmail."', CAST(10 AS Decimal(2, 0)), CAST(N'2022-07-23T10:30:00.000' AS DateTime), CAST(N'2022-07-23T12:00:00.000' AS DateTime), N'9404d710-8547-4252-8404-30d7c518de45')
